INSERT INTO `page_templates` (`id`, `name`, `content`, `header_content`, `created_at`, `updated_at`) VALUES (1,'Homepage','<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Strict//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"en\" lang=\"en\">\r\n  <head>\r\n    <meta http-equiv=\"content-type\" content=\"text/html; charset=UTF-8\" />\r\n    <title>\r\n      Welcome | The Official Site of the Lost City of Trellech Project\r\n    </title>\r\n    <meta name=\"keywords\" content=\"trelech, trelleck, treleck, excavation project, archaeology, stuart wilson\" />\r\n    <meta name=\"robots\" content=\"all\" />\r\n    <meta name=\"generator\" content=\"Microsoft FrontPage 5.0\" />\r\n    <link rel=\"stylesheet\" type=\"text/css\" href=\"/site_files/stylesheets/main.css\" title=\"Lost\" />\r\n    <!--[if IE]>\r\n  <link rel=\"stylesheet\" type=\"text/css\" href=\"/site_files/stylesheets/ie.css\" title=\"LostIE\" />\r\n<![endif]-->\r\n<!-- Start of StatCounter Code -->\r\n<script type=\"text/javascript\" language=\"javascript\">\r\nvar sc_project=1475064;\r\nvar sc_invisible=1;\r\nvar sc_partition=13;\r\nvar sc_security=\"ac046131\";\r\n        </script>\r\n<script type=\"text/javascript\" language=\"javascript\" src=\"http://www.statcounter.com/counter/counter.js\"></script><noscript>\r\n        <a href=\"http://www.statcounter.com/\" target=\"_blank\"><img  src=\"http://c14.statcounter.com/counter.php?sc_project=1475064&java=0&security=ac046131&invisible=1\" alt=\"website statistics\" border=\"0\"></a></noscript>\r\n<!-- End of StatCounter Code -->\r\n  </head>\r\n  <body class=\"sandvox no-sidebar home-page\" id=\"www_lostcityoftrellech_co_uk\" >\r\n<div id=\"page-container\" class=\"text-page\">\r\n<div id=\"page\">\r\n<div id=\"page-top\">\r\n<div id=\"title\">\r\n<h1><a href=\"/\">The Official Site of the Lost City of Trellech Project</a></h1>\r\n<p>For all the latest news from the Lost City of Trellech excavation site.</p>\r\n</div>\r\n<!-- title -->\r\n<div id=\"sitemenu-container\">\r\n<div id=\"sitemenu\">\r\n<div id=\"sitemenu-content\">\r\n#MENU1#\r\n</div>\r\n<!-- sitemenu-content --></div>\r\n<!-- sitemenu --></div>\r\n<!-- sitemenu-container --></div>\r\n<!-- page-top -->\r\n<div id=\"page-content\">\r\n<div id=\"main\">\r\n<div id=\"main-content\"><!-- Text Page Contents -->\r\n<div class=\"article\">\r\n<div class=\"article-content\"><!-- Rich Text Element --> #CONTENT#<br style=\"clear: both; height: 0px; line-height: 0px; font-size: 0px;\" /></div>\r\n<!-- article-content --> <!-- article-info --></div>\r\n<!-- article --> <!-- /Text Page Contents --></div>\r\n<!-- main-content --></div>\r\n<!-- main --></div>\r\n<!-- content -->\r\n<div id=\"page-bottom\">\r\n<div id=\"footer\">Copyright Stuart Wilson</div>\r\n<!-- page-bottom --></div>\r\n<!-- container --></div>\r\n<!-- specific body type --></div>\r\n<script type=\"text/javascript\">// <![CDATA[\r\nvar gaJsHost = ((\"https:\" == document.location.protocol) ? \"https://ssl.\" : \"http://www.\");\r\ndocument.write(unescape(\"%3Cscript src=\'\" + gaJsHost + \"google-analytics.com/ga.js\' type=\'text/javascript\'%3E%3C/script%3E\"));\r\n// ]]></script>\r\n<script type=\"text/javascript\">// <![CDATA[\r\ntry {\r\nvar pageTracker = _gat._getTracker(\"UA-1767493-4\");\r\npageTracker._trackPageview();\r\n} catch(err) {}\r\n// ]]></script>\r\n</body>\r\n</html>','<meta name=\"google-site-verification\" content=\"zkqzCzONdiTo04hai_TZB3y_qcv0rtS6RhSNphlorH8\" />','2009-12-16 01:46:39','2011-05-09 22:04:16');
